package org.apache.pivot.tutorials.databinding;

public class Contact {
    private String id;
    private String name;
    private Address address;
    private String phoneNumber;
    private String emailAddress;
    private IMAccount imAccount;

    public Contact(String id, String name, Address address, String phoneNumber,
        String emailAddress, IMAccount imAccount) {
        this.id = id;
        this.name = name;
        this.address = address;
        this.phoneNumber = phoneNumber;
        this.emailAddress = emailAddress;
        this.imAccount = imAccount;
    }

    public String getID() {
        return id;
    }

    public String getId() {
        return getID();
    }

    public String getName() {
        return name;
    }

    public Address getAddress() {
        return address;
    }

    public String getPhoneNumber() {
        return phoneNumber;
    }

    public String getEmailAddress() {
        return emailAddress;
    }

    public IMAccount getIMAccount() {
        return imAccount;
    }

    public IMAccount getImAccount() {
        return getIMAccount();
    }
}


package org.apache.pivot.tutorials.databinding;

public class Address {
    private String street;
    private String city;
    private String state;
    private String zip;

    public Address() {
        this(null, null, null, null);
    }

    public Address(String street, String city, String state, String zip) {
        this.street = street;
        this.city = city;
        this.state = state;
        this.zip = zip;
    }

    public String getStreet() {
        return street;
    }

    public String getCity() {
        return city;
    }

    public String getState() {
        return state;
    }

    public String getZip() {
        return zip;
    }
}


public class IMAccount {
    private String id;
    private String type;

    public IMAccount() {
        this(null, null);
    }

    public IMAccount(String id, String type) {
        this.id = id;
        this.type = type;
    }

    public String getID() {
        return id;
    }

    public String getId() {
        return getID();
    }

    public String getType() {
        return type;
    }
}
